query:
query GetRecommendationProducts($input: PublicRecommendationInput) {
  getRecommendationProducts(input: $input) {
    products {
      imageUrls
      title
      price
      currency
      compareAtPrice
      externalId
      productUrl
      variantId
      variants {
        id
        external_id
        options {
          name
          value
        }
        available_quantity
        sku
        price {
          currency
          amount
        }
        title
        compare_at_price {
          currency
          amount
        }
        available
        image_url
        image_urls
        available_quantity
      }
      options {
        name
        values
      }
      media {
        id
        external_product_media_id
        type
        sort
        thumbnail {
          url
        }
        url
        mime_type
        external_video_host
      }
    }
    campaign {
      status
      campaign_id
      campaign_title
      location
      presentation_settings {
        layout {
          products_of_row
          number_of_products
          image_shape
          image_height
          image_max_width
        }
        desktop_layout {
          display_style
          media_width
          columns
          number_of_products
        }
        mobile_layout {
          display_style
          columns
        }
        product_info {
          alignment
          image_crop
          image_radius
        }
        color {
          product_name
          product_price
          compare_at_price
          action_button_background
          action_button_text
          widget_background
          campaign_title_text
        }
        content {
          show_product_name
          show_product_price
          show_compare_at_price
          action_button_text {
            text
            enabled
          }
          action_button {
            text
            enabled
            button_type
            button_action
            product_variant_picker_type
          }
        }
        font {
          heading_text_font_size
          heading_text_font_family
          product_info_text_font_family
        }
        padding {
          top
          bottom
        }
      }
    }
  }
}
